import{_ as e,o,c as s,a as t,e as n}from"./app.1e6b6c66.js";const r="/xuanmu-blogs/assets/img-2023-06-27-10-04-51.2c9ca83b.png",k=JSON.parse('{"title":"3.1 52 North WPS","description":"第二章：现有WPS框架","frontmatter":{"title":"3.1 52 North WPS","description":"第二章：现有WPS框架","date":"2023-6-27","tags":["OGC WPS理论方法及开发实践"]},"headers":[{"level":2,"title":"3.1 52 North WPS","slug":"_3-1-52-north-wps","link":"#_3-1-52-north-wps","children":[]}],"relativePath":"knowledge/专著_第三章：现有WPS框架/3.1 52 North WPS.md"}'),_={name:"knowledge/专著_第三章：现有WPS框架/3.1 52 North WPS.md"},a=t("h2",{id:"_3-1-52-north-wps",tabindex:"-1"},[n("3.1 52 North WPS "),t("a",{class:"header-anchor",href:"#_3-1-52-north-wps","aria-hidden":"true"},"#")],-1),c=t("p",null,"高效的地理处理",-1),l=t("p",null,"随着新的、低成本的观测技术的发展，关于环境的时空数据量正在迅速增加。由于可用的数据量及其在格式、元数据、存储和接口方面的异构性，传统桌面地理信息系统中提供的地理处理工具已不再适用，需要新的方法来进行分布式地理处理。",-1),h=t("p",null,"北纬52°地理处理开放实验室解决了这些问题，并为在不同技术环境中共享、分发和集成地理处理工具开发了新的解决方案。这些工具的范围可以从简单的运算符，例如缓冲线段，到依赖空间信息的复杂环境模型，例如利用现场和卫星观测的洪水预测模型。",-1),i=t("p",null,"该实验室的一个核心重点是通过OGC网络处理服务(WPS)标准将地理处理设施整合到空间数据基础设施中。52°North引领了OGC WPS 2.0版的标准化进程，并提供了一个基于Java的实施--52°North Web处理服务，该服务具有用于流程和数据编码的可插拔架构，并配有多个现成可用的流程存储库。",-1),d=t("p",null,"假设地理处理工具可以很容易地在空间数据基础设施中的不同位置共享和灵活部署，那么如何以用户友好的方式找到和执行这些工具的问题仍然没有解决。因此，52n为地理处理工具的发现、编排和执行开发新的概念和客户端。",-1),p=t("p",null,"处理大量的时空数据需要进行前处理和后处理。在数据附近部署地理处理工具，而不是将数据传输到运行地理处理工具的位置，也需要新的方法。因此，另一个挑战是确定大数据集的必要的前处理和后处理步骤，并开发用于比较和优化分布式地理处理的体系结构的概念。",-1),u=t("p",null,[t("img",{src:r,alt:""})],-1),P=[a,c,l,h,i,d,p,u];function S(W,m,N,g,f,w){return o(),s("div",null,P)}const b=e(_,[["render",S]]);export{k as __pageData,b as default};
